mapper.select:

    	select * from (
    		select 
    		rownum rn, bno, title, content, writer, regdate, updatedate, category, catename  
   		from (
	        select /*+ INDEX_DESC(tbl_board pk_board) */ 
	        	bno, title, content, writer, regdate, updatedate, category, catename
	        	from tbl_board where category= #{category} and bno>0
	    	) where 



	    	rownum <= #{pageNum} * #{amount} 
		) where rn > (#{pageNum} - 1) * #{amount}
